INSERT INTO `wp_posts` VALUES (1,1,'2019-01-26 11:16:13','2019-01-26 11:16:13','<!-- wp:paragraph -->\n<p>Welcome to WordPress. This is your first post. Edit or delete it, then start writing!</p>\n<!-- /wp:paragraph -->','News Post 1','','publish','open','open','','news-post-1','','','2019-01-29 09:30:07','2019-01-29 09:30:07','',0,'http://beta.whitetreehr.co.uk/?p=1',0,'post','',0),(2,1,'2019-01-26 11:16:13','2019-01-26 11:16:13','<!-- wp:heading -->\n<h2>WELCOME TO WHITETREE HR CONSULTANCY</h2>\n<!-- /wp:heading -->','Home','','publish','closed','closed','','sample-page','','','2019-01-29 16:05:43','2019-01-29 16:05:43','',0,'http://beta.whitetreehr.co.uk/?page_id=2',0,'page','',0),(34,1,'2019-01-29 09:08:32','2019-01-29 09:08:32','','Home','','inherit','closed','closed','','2-revision-v1','','','2019-01-29 09:08:32','2019-01-29 09:08:32','',2,'http://beta.whitetreehr.co.uk/2-revision-v1/',0,'revision','',0),(38,1,'2019-01-29 09:13:27','2019-01-29 09:13:27','a:7:{s:8:\"location\";a:2:{i:0;a:1:{i:0;a:3:{s:5:\"param\";s:4:\"page\";s:8:\"operator\";s:2:\"==\";s:5:\"value\";s:1:\"2\";}}i:1;a:1:{i:0;a:3:{s:5:\"param\";s:4:\"page\";s:8:\"operator\";s:2:\"==\";s:5:\"value\";s:2:\"23\";}}}s:8:\"position\";s:15:\"acf_after_title\";s:5:\"style\";s:8:\"seamless\";s:15:\"label_placement\";s:3:\"top\";s:21:\"instruction_placement\";s:5:\"label\";s:14:\"hide_on_screen\";s:0:\"\";s:11:\"description\";s:0:\"\";}','Services Output','services-output','publish','closed','closed','','group_5c5018e27eb7b','','','2019-01-29 09:13:48','2019-01-29 09:13:48','',0,'http://beta.whitetreehr.co.uk/?post_type=acf-field-group&#038;p=38',0,'acf-field-group','',0),(39,1,'2019-01-29 09:13:27','2019-01-29 09:13:27','a:12:{s:4:\"type\";s:12:\"relationship\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:0:\"\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:9:\"post_type\";a:1:{i:0;s:7:\"service\";}s:8:\"taxonomy\";s:0:\"\";s:7:\"filters\";a:1:{i:0;s:6:\"search\";}s:8:\"elements\";s:0:\"\";s:3:\"min\";s:0:\"\";s:3:\"max\";s:0:\"\";s:13:\"return_format\";s:6:\"object\";}','Services','services','publish','closed','closed','','field_5c5018f922b69','','','2019-01-29 09:13:48','2019-01-29 09:13:48','',38,'http://beta.whitetreehr.co.uk/?post_type=acf-field&#038;p=39',0,'acf-field','',0),(40,1,'2019-01-29 09:14:02','2019-01-29 09:14:02','','Home','','inherit','closed','closed','','2-revision-v1','','','2019-01-29 09:14:02','2019-01-29 09:14:02','',2,'http://beta.whitetreehr.co.uk/2-revision-v1/',0,'revision','',0),(3,1,'2019-01-26 11:16:13','2019-01-26 11:16:13','<!-- wp:heading --><h2>Who we are</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Our website address is: http://beta.whitetreehr.co.uk.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>What personal data we collect and why we collect it</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Comments</h3><!-- /wp:heading --><!-- wp:paragraph --><p>When visitors leave comments on the site we collect the data shown in the comments form, and also the visitor&#8217;s IP address and browser user agent string to help spam detection.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>An anonymised string created from your email address (also called a hash) may be provided to the Gravatar service to see if you are using it. The Gravatar service privacy policy is available here: https://automattic.com/privacy/. After approval of your comment, your profile picture is visible to the public in the context of your comment.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Media</h3><!-- /wp:heading --><!-- wp:paragraph --><p>If you upload images to the website, you should avoid uploading images with embedded location data (EXIF GPS) included. Visitors to the website can download and extract any location data from images on the website.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Contact forms</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Cookies</h3><!-- /wp:heading --><!-- wp:paragraph --><p>If you leave a comment on our site you may opt-in to saving your name, email address and website in cookies. These are for your convenience so that you do not have to fill in your details again when you leave another comment. These cookies will last for one year.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>If you have an account and you log in to this site, we will set a temporary cookie to determine if your browser accepts cookies. This cookie contains no personal data and is discarded when you close your browser.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>When you log in, we will also set up several cookies to save your login information and your screen display choices. Login cookies last for two days, and screen options cookies last for a year. If you select &quot;Remember Me&quot;, your login will persist for two weeks. If you log out of your account, the login cookies will be removed.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>If you edit or publish an article, an additional cookie will be saved in your browser. This cookie includes no personal data and simply indicates the post ID of the article you just edited. It expires after 1 day.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Embedded content from other websites</h3><!-- /wp:heading --><!-- wp:paragraph --><p>Articles on this site may include embedded content (e.g. videos, images, articles, etc.). Embedded content from other websites behaves in the exact same way as if the visitor has visited the other website.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>These websites may collect data about you, use cookies, embed additional third-party tracking, and monitor your interaction with that embedded content, including tracking your interaction with the embedded content if you have an account and are logged in to that website.</p><!-- /wp:paragraph --><!-- wp:heading {\"level\":3} --><h3>Analytics</h3><!-- /wp:heading --><!-- wp:heading --><h2>Who we share your data with</h2><!-- /wp:heading --><!-- wp:heading --><h2>How long we retain your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p>If you leave a comment, the comment and its metadata are retained indefinitely. This is so we can recognise and approve any follow-up comments automatically instead of holding them in a moderation queue.</p><!-- /wp:paragraph --><!-- wp:paragraph --><p>For users that register on our website (if any), we also store the personal information they provide in their user profile. All users can see, edit, or delete their personal information at any time (except they cannot change their username). Website administrators can also see and edit that information.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>What rights you have over your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p>If you have an account on this site, or have left comments, you can request to receive an exported file of the personal data we hold about you, including any data you have provided to us. You can also request that we erase any personal data we hold about you. This does not include any data we are obliged to keep for administrative, legal, or security purposes.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Where we send your data</h2><!-- /wp:heading --><!-- wp:paragraph --><p>Visitor comments may be checked through an automated spam detection service.</p><!-- /wp:paragraph --><!-- wp:heading --><h2>Your contact information</h2><!-- /wp:heading --><!-- wp:heading --><h2>Additional information</h2><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>How we protect your data</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>What data breach procedures we have in place</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>What third parties we receive data from</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>What automated decision making and/or profiling we do with user data</h3><!-- /wp:heading --><!-- wp:heading {\"level\":3} --><h3>Industry regulatory disclosure requirements</h3><!-- /wp:heading -->','Privacy Policy','','draft','closed','open','','privacy-policy','','','2019-01-26 11:16:13','2019-01-26 11:16:13','',0,'http://beta.whitetreehr.co.uk/?page_id=3',0,'page','',0),(5,1,'2019-01-26 11:18:56','2019-01-26 11:18:56','[text* your-name placeholder \"Please tell us your name\"]\r\n[email* your-email placeholder \"What\'s your e-mail address?\"]\r\n[tel telephone placeholder \"What number is best to call you on?\"]\r\n[textarea your-message placeholder \"Ask us anything!\"]\r\n[text how-hear placeholder \"How did you hear about us?\"]\r\n[submit \"Send message\"]\n1\nWhite Tree HR \"[your-subject]\"\n[your-name] <wordpress@beta.whitetreehr.co.uk>\nsteve.deveaux@blueyonder.co.uk\nFrom: [your-name] <[your-email]>\r\nSubject: [your-subject]\r\n\r\nMessage Body:\r\n[your-message]\r\n\r\n-- \r\nThis e-mail was sent from a contact form on White Tree HR (http://beta.whitetreehr.co.uk)\nReply-To: [your-email]\n\n\n\n\nWhite Tree HR \"[your-subject]\"\nWhite Tree HR <wordpress@beta.whitetreehr.co.uk>\n[your-email]\nMessage Body:\r\n[your-message]\r\n\r\n-- \r\nThis e-mail was sent from a contact form on White Tree HR (http://beta.whitetreehr.co.uk)\nReply-To: steve.deveaux@blueyonder.co.uk\n\n\n\nThank you for your message. It has been sent.\nThere was an error trying to send your message. Please try again later.\nOne or more fields have an error. Please check and try again.\nThere was an error trying to send your message. Please try again later.\nYou must accept the terms and conditions before sending your message.\nThe field is required.\nThe field is too long.\nThe field is too short.\nThe date format is incorrect.\nThe date is before the earliest one allowed.\nThe date is after the latest one allowed.\nThere was an unknown error uploading the file.\nYou are not allowed to upload files of this type.\nThe file is too big.\nThere was an error uploading the file.\nThe number format is invalid.\nThe number is smaller than the minimum allowed.\nThe number is larger than the maximum allowed.\nThe answer to the quiz is incorrect.\nYour entered code is incorrect.\nThe e-mail address entered is invalid.\nThe URL is invalid.\nThe telephone number is invalid.','Contact form','','publish','closed','closed','','contact-form-1','','','2019-01-30 12:30:41','2019-01-30 12:30:41','',0,'http://beta.whitetreehr.co.uk/?post_type=wpcf7_contact_form&#038;p=5',0,'wpcf7_contact_form','',0),(35,1,'2019-01-29 09:10:23','2019-01-29 09:10:23','a:7:{s:8:\"location\";a:1:{i:0;a:1:{i:0;a:3:{s:5:\"param\";s:4:\"page\";s:8:\"operator\";s:2:\"==\";s:5:\"value\";s:1:\"2\";}}}s:8:\"position\";s:15:\"acf_after_title\";s:5:\"style\";s:8:\"seamless\";s:15:\"label_placement\";s:3:\"top\";s:21:\"instruction_placement\";s:5:\"label\";s:14:\"hide_on_screen\";a:1:{i:0;s:11:\"the_content\";}s:11:\"description\";s:0:\"\";}','Banner','banner','publish','closed','closed','','group_5c50183c2c0f0','','','2019-01-29 16:04:51','2019-01-29 16:04:51','',0,'http://beta.whitetreehr.co.uk/?post_type=acf-field-group&#038;p=35',0,'acf-field-group','',0),(36,1,'2019-01-29 09:10:23','2019-01-29 09:10:23','a:15:{s:4:\"type\";s:5:\"image\";s:12:\"instructions\";s:0:\"\";s:8:\"required\";i:0;s:17:\"conditional_logic\";i:0;s:7:\"wrapper\";a:3:{s:5:\"width\";s:2:\"25\";s:5:\"class\";s:0:\"\";s:2:\"id\";s:0:\"\";}s:13:\"return_format\";s:5:\"array\";s:12:\"preview_size\";s:9:\"thumbnail\";s:7:\"library\";s:3:\"all\";s:9:\"min_width\";s:0:\"\";s:10:\"min_height\";s:0:\"\";s:8:\"min_size\";s:0:\"\";s:9:\"max_width\";s:0:\"\";s:10:\"max_height\";s:0:\"\";s:8:\"max_size\";s:0:\"\";s:10:\"mime_types\";s:0:\"\";}','Banner Image','banner_image','publish','closed','closed','','field_5c501851242d7','','','2019-01-29 09:10:23','2019-01-29 09:10:23','',35,'http://beta.whitetreehr.co.uk/?post_type=acf-field&p=36',0,'acf-field','',0),(37,1,'2019-01